mapper.select:
SELECT persona FROM relacion_clave_finsol WHERE persona=
#{persona}
UNION
SELECT persona FROM relacion_clave_finsol WHERE
clave_finsol = #{persona}
UNION
SELECT persona FROM
relacion_clave_finsol WHERE clave_cu = #{persona}

SELECT sb.solicitud , sb.persona, sb.folio_consulta_buro as folioConsultaBuro, sb.status, p.nombre , p.apellido_pat as apellidoPat, p.apellido_mat as apellidoMat, p.nombre + ' ' + p.apellido_pat + ' ' + p.apellido_mat AS nombreCompleto, convert(varchar(10), p.fecha_nacimiento, 103) AS fechaNacimiento, p.ciclo, CONVERT(INT,DATEDIFF(DAY, p.fecha_nacimiento, GETDATE())/365.25) AS edad, si.cargo_mesa_directiva AS rol, sf.motivo_cancelacion AS motivoCancelacion, si.contrato_anterior AS contratoAnterior FROM solicitud_buro sb INNER JOIN personas p ON p.persona = sb.persona INNER JOIN solicitudes_finsol sf ON sb.solicitud = sf.solicitud INNER JOIN solicitud_integrantes si ON sb.persona = si.cliente AND si.solicitud = sf.solicitud AND si.status = 'A' WHERE sb.solicitud = #{solicitud}
AND sb.persona= #{persona}
AND sb.status = #{status}

SELECT rcvf.clave_cu AS customerId, pers.nombre AS firstName, '' AS otherName, pers.apellido_pat AS secondName, pers.apellido_mat AS lastName, pers.sexo AS gender, ISNULL( Convert(nvarchar,pers.fecha_nacimiento,103), '') AS birthdate, pers.rfc AS rfc, pers.persona as persona FROM personas pers, relacion_clave_finsol rcvf WHERE pers.persona = rcvf.persona AND rcvf.clave_cu IS NOT null
AND pers.nombre LIKE #{pNombre}||'%'
AND pers.apellido_pat = #{pAPaterno}
AND pers.apellido_mat = #{pAMaterno}
AND pers.rfc LIKE #{rfc}||'%'
AND pers.fecha_nacimiento = #{fechaNacimiento}

SELECT solf.solicitud AS idSolicitud, sucu.sucursal AS cveSucursal, sucu.nombre AS sucursal, solf.vendedor AS cdgAsesor, solf.codigo_producto +' - '+ prod.descripcion AS producto, solf.cliente AS codigoGrupo, pers.nombre AS nombreGrupo, solf.efectivo_solicitado AS montoSolicitado, ISNULL( Convert(nvarchar, solf.fecha_captura, 103), '') AS fechaSolicitud, ISNULL( Convert(nvarchar, solf.fecha_resolucion, 103), '') AS fechaDesembolso, ISNULL(ccvf.contrato_LD, '') AS contratoPrevio, pera.nombre + ' ' + pera.apellido_pat + ' ' + pera.apellido_mat AS nombreAsesor FROM solicitudes_finsol solf INNER JOIN personas pers ON(pers.persona = solf.cliente) INNER JOIN sucursales sucu ON(sucu.sucursal = solf.oficina) INNER JOIN productos prod ON(prod.producto = solf.codigo_producto) LEFT JOIN contrato_clave_finsol ccvf ON(ccvf.contrato = solf.contrato_ant) LEFT JOIN personas pera ON pera.persona = solf.vendedor WHERE solf.status = 'A'
AND solf.oficina = #{pSucursalInt}
AND pers.nombre = #{pNombreGrupo}
AND solf.solicitud = #{solicitud}
ORDER BY solf.solicitud

SELECT solf.solicitud AS solicitud, solf.cliente AS noPersonaGrupo, rcvf.clave_finsol AS noGrupo, pers.nombre AS nombreGrupo, ciclo = (CASE WHEN solf.contrato IS NOT NULL AND solf.contrato != '' THEN solf.ciclo + 1 ELSE solf.ciclo END), solf.numero_integrantes AS noIntegrantes, Convert(nvarchar,sucu.sucursal) +' - '+ sucu.nombre AS sucursal, solf.codigo_producto + ' - '+ isnull(prod.descripcion, ' ') AS producto, ISNULL(ccvf.contrato_LD, '') AS LD, ISNULL(ccvf.contrato, '') AS contrato, ISNULL( Convert(nvarchar, solf.fecha_captura, 103), '') AS fechaSolicitud, solf.efectivo_solicitado AS montoSolicitado, (CASE WHEN solf.contrato IS NOT NULL AND solf.contrato != '' THEN solf.efectivo_solicitado ELSE solf.efectivo_otorgado END ) AS montoAprobado, (CASE WHEN solf.contrato IS NOT NULL AND solf.contrato != '' THEN 'AUTORIZADO' WHEN solf.etapa = 'true' THEN 'REVISION' ELSE 'CAPTURA' END ) AS estatus, 'SACG' AS sistema FROM solicitudes_finsol solf INNER JOIN personas pers ON pers.persona = solf.cliente INNER JOIN sucursales sucu ON sucu.sucursal = solf.oficina INNER JOIN relacion_clave_finsol rcvf ON rcvf.persona = solf.cliente LEFT OUTER JOIN contrato_clave_finsol ccvf ON ccvf.contrato = solf.contrato LEFT OUTER JOIN productos prod ON prod.producto = solf.codigo_producto WHERE solf.fecha_captura BETWEEN #{pFechaInicio} AND #{pFechaFin} AND solf.status != 'X'
AND solf.oficina = #{pSucursalInt}
AND ( (solf.fecha_resolucion > DATEADD(day, -5, #{fechaContable}) AND solf.contrato IS NOT NULL AND solf.contrato != '' ) OR (solf.contrato IS NULL) OR (solf.contrato = '') )
AND ( solf.fecha_resolucion > DATEADD(day, -5, #{fechaContable}) AND solf.contrato IS NOT NULL AND solf.contrato != '' )
AND ( solf.etapa = 'true' AND (solf.contrato IS NULL OR solf.contrato = '') )
AND ( solf.etapa != 'true' AND (solf.contrato IS NULL OR solf.contrato = '') )
AND solf.ciclo = #{pCicloInt}
AND solf.codigo_producto = #{pProducto}

SELECT a.id_atomo as idAtomo, a.proceso, a.id_proceso as idProceso, a.id_tarea as idTarea, a.solicitud, a.cliente, a.usuario_genero_atomo as usuarioGeneroAtomo, CASE WHEN usuario_genero_atomo = '000000043' THEN 'SISTEMA' ELSE p.nombre || ' ' || p.apellido_pat || ' ' || p.apellido_mat END AS nombreUsuarioGeneroAtomo, a.status, a.tipo_atomo as tipoAtomo, a.es_critico as esCritico, a.etapa, ISNULL(CONVERT(VARCHAR(10),a.hora_registro,103),'') + ' ' + ISNULL(CONVERT(VARCHAR(10),a.hora_registro,108),'') AS horaRegistro, a.hora_resolucion as horaResolucion, a.codigo_resolucion as codigoResolucion, a.resolucion, a.comentario, a.id_atomo_anterior as idAtomoAnterior, a.genero_atomo_siguiente as generoAtomoSiguiente, a.origen_error as origenError, a.numero_integrante as numeroIntegrante FROM atomos a LEFT JOIN personas p ON p.persona = a.usuario_genero_atomo WHERE solicitud = #{solicitud}
ORDER BY a.hora_registro desc
ORDER BY a.hora_registro asc

SELECT sf.solicitud AS solicitud, isnull(sf.estatus, ' ') AS estatus, isnull(sf.descripcion, ' ') AS subEstatus, pg.nombre AS nombreGrupo, isnull(ccf.contrato_LD, ' ') AS contratoLD, isnull(ccf.contrato, ' ') AS contrato, sf.codigo_producto AS codigoProducto, prod.descripcion AS descripcion, bt.usuario_propietario AS asesor, p.nombre || ' ' || p.apellido_pat || ' ' || p.apellido_mat AS nombreAsesor, sf.express AS express, sf.categoria AS categoria, isnull(sf.motivo_cancelacion, ' ') AS motivoCancelacion, isnull(ted.motivo_excepcion, ' ') AS motivoJustificacion, isnull(cod.descripcion, ' ') AS campana FROM solicitudes_finsol sf LEFT JOIN buzon_tareas bt ON (bt.descripcion = sf.solicitud AND bt.perfil = 'PCEC') LEFT JOIN contratos c ON c.contrato = sf.contrato LEFT JOIN personas pg ON pg.persona = sf.cliente LEFT JOIN productos prod ON prod.producto = sf.codigo_producto LEFT JOIN sucursales s ON s.sucursal = sf.oficina LEFT JOIN regiones r ON r.region = s.region LEFT JOIN divisiones d ON d.division = r.division LEFT JOIN personas p ON p.persona = bt.usuario_propietario LEFT JOIN contrato_clave_finsol ccf ON ccf.contrato = sf.contrato LEFT JOIN tareas_excepcion_documental ted ON ted.solicitud = sf.solicitud INNER JOIN codigos cod ON cod.tipo_codigo = 'CECG' AND cod.codigo = sf.campana WHERE 1 = 1
AND sf.solicitud = #{solicitud}
AND sf.oficina = #{sucursal}
AND sf.fecha_captura BETWEEN #{desde} AND #{hasta}
AND sf.estatus = #{estatus}
AND r.region = #{region}
AND d.division = #{division}
ORDER BY sf.fecha_captura desc

SELECT count(*) FROM buzon_tareas
WHERE id_tarea_padre = #{1}
AND status = 'P'

SELECT solf.solicitud, grup.persona AS grupoPersona, rcvf.clave_finsol AS grupo, grup.nombre AS nombreGrupo, CASE WHEN solf.codigo_producto != 'GOS1' THEN ISNULL(solf.ciclo, 0) + 1 ELSE ISNULL(solfOP.ciclo, 0) END AS ciclo, solf.numero_integrantes AS integrantes, CONVERT(VARCHAR(10), sucu.sucursal) + ' - ' + sucu.nombre AS sucursal, prod.producto + ' - ' + prod.descripcion AS producto, ISNULL(camp.descripcion, '') AS campanaEspecial, ISNULL(ccvf.contrato_LD, '') AS contratoLD, ISNULL(CONVERT(nvarchar, solf.fecha_captura, 103), '') + ' ' + ISNULL(CONVERT(nvarchar, solf.hora_captura, 108), '') AS fechaCaptura, solf.efectivo_solicitado AS montoSolicitado, CASE WHEN ccvf.contrato_LD IS NOT null THEN solf.efectivo_solicitado ELSE 0.00 END AS montoAprobado, CASE WHEN solf.estatus IS NULL OR solf.estatus = '' THEN 'CAPTURA' ELSE solf.estatus END AS estatus, isnull(solf.descripcion, ' ') AS subEstatus, aseR.nombre + ' ' + aseR.apellido_pat + ' ' + aseR.apellido_mat AS analistaCECAsignado, isnull(aseR.persona, ' ') AS personaAnaCECAsig, aseA.nombre + ' ' + aseA.apellido_pat + ' ' + aseA.apellido_mat AS analistaCECAutorizo, ISNULL(CONVERT(nvarchar, buzR.fecha_asignacion, 103), '') + ' ' + ISNULL(CONVERT(nvarchar, buzR.fecha_asignacion, 108), '') AS fechaAsignacion, ISNULL(tipi.descripcion, 'S/TIPIFICAR') AS tipoSucursal, zona.nombre AS zona, divi.nombre AS division, CASE WHEN solf.hora_resolucion = '1900-02-01' THEN '' WHEN ISNULL(CONVERT(nvarchar, solf.hora_resolucion, 103), '') = '01/02/1900' THEN '' ELSE ISNULL(CONVERT(nvarchar, solf.hora_resolucion, 103), '') + ' ' + ISNULL(CONVERT(nvarchar, solf.hora_resolucion, 108), '') END AS fechaAprobacion, ISNULL(CONVERT(nvarchar, atom.hora_registro, 103), '') + ' ' + ISNULL(CONVERT(nvarchar, atom.hora_registro, 108), '') AS fechaObservaciones, CASE WHEN tecampania.solicitud != NULL THEN 'Excepcin de Restriccin' ELSE str_replace(excep.descripcion,',',null) END AS marcaEspecialCEC, tec.ponderacion AS ponderacion, btr.fecha_reasigna AS fechaReasignacion, prea.nombre + ' ' + prea.apellido_pat + ' ' + prea.apellido_mat AS analistaReasigno, ISNULL(CONVERT(nvarchar, atomu.hora_registro, 103), '') + ' ' + ISNULL(CONVERT(nvarchar, atomu.hora_registro, 108), '') AS fechaUltObserv, solf.categoria AS renoFinanciada, CASE WHEN solf.express = 'S' THEN 'EXPRESS' ELSE ' ' END AS renovacionExpress, CASE WHEN (isnull(ltrim(solf.estatus),'') = '' OR solf.estatus = 'ORIGEN') AND isnull(solf.express,'N') != 'S' THEN 'CAPTURA' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(buzR.usuario_propietario, '') = '' AND isnull(solf.express,'N') != 'S' THEN 'CAPTURA' WHEN (isnull(ltrim(solf.estatus),'') = '' OR solf.estatus = 'ORIGEN') AND isnull(solf.express,'N') = 'S' THEN 'CAPTURA EXPRESS' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(buzR.usuario_propietario, '') = '' AND isnull(solf.express,'N') = 'S' THEN 'CAPTURA EXPRESS' WHEN solf.estatus = 'GENERA_CONTRATO' THEN 'AUTORIZADO' WHEN solf.estatus = 'EXCEPCION_GARANTIA' AND isnull(solf.descripcion, '') = 'REVISION' THEN 'BUZN DIVISIONAL' WHEN solf.estatus = 'BURO' THEN 'BURO' WHEN solf.estatus = 'CANCELADA' THEN 'CANCELADA' WHEN solf.estatus = 'OBSERVACIONES_CEC' AND rtrim(ltrim(isnull(solf.descripcion,''))) = 'EVCR_ERROR' THEN 'OBSERVACIONES POR ATENDER (SUCURSAL)' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(atom.hora_registro,'') != '' THEN 'OBSERVACIONES POR ATENDER (SUCURSAL)' WHEN solf.estatus = 'EVALUACION_CEC' AND solf.descripcion = 'EVCR_OK' AND isnull(solf.contrato,'') = '' THEN 'FILA DE AUTORIZACIN' WHEN solf.estatus = 'ASIGNACION_CEC' AND isnull(atom.hora_registro,'') = '' THEN 'REVISIN INICIAL' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(atom.hora_registro,'') = '' THEN 'REVISIN INICIAL' WHEN solf.estatus = 'EVALUACION_CEC' AND isnull(atom.hora_registro,'') = '' AND isnull(buzR.usuario_propietario, '') != '' THEN 'REVISIN INICIAL' WHEN solf.estatus = 'EXCEPCION_GARANTIA' AND isnull(solf.descripcion, ' ') = 'APROBADA' AND isnull(aseR.persona, '') != '' AND isnull(ltrim(solf.contrato),'') = '' THEN 'REVISIN INICIAL' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(atom.hora_registro,'') != '' THEN 'REVISIN RESPUESTA' WHEN solf.estatus = 'ASIGNACION_CEC' AND isnull(solf.descripcion,'') = 'SUCU_CORRE' THEN 'REVISIN RESPUESTA' ELSE solf.estatus END AS estatusReal,
CASE WHEN (isnull(ltrim(solf.estatus),'') = '' OR solf.estatus = 'ORIGEN') AND isnull(solf.express,'N') != 'S' THEN '0001' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(buzR.usuario_propietario, '') = '' AND isnull(solf.express,'N') != 'S' THEN '0001' WHEN (isnull(ltrim(solf.estatus),'') = '' OR solf.estatus = 'ORIGEN') AND isnull(solf.express,'N') = 'S' THEN '0002' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(buzR.usuario_propietario, '') = '' AND isnull(solf.express,'N') = 'S' THEN '0002' WHEN solf.estatus = 'GENERA_CONTRATO' THEN '0009' WHEN solf.estatus = 'EXCEPCION_GARANTIA' AND isnull(solf.descripcion, ' ') = 'REVISION' THEN '0004' WHEN solf.estatus = 'BURO' THEN '0003' WHEN solf.estatus = 'CANCELADA' THEN '0010' WHEN solf.estatus = 'OBSERVACIONES_CEC' AND rtrim(ltrim(isnull(solf.descripcion,''))) = 'EVCR_ERROR' THEN '0006' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(atom.hora_registro,'') != '' THEN '0006' WHEN solf.estatus = 'EVALUACION_CEC' AND solf.descripcion = 'EVCR_OK' AND isnull(solf.contrato,'') = '' THEN '0008' WHEN solf.estatus = 'ASIGNACION_CEC' AND isnull(atom.hora_registro,'') = '' THEN '0005' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(atom.hora_registro,'') = '' THEN '0005' WHEN solf.estatus = 'EVALUACION_CEC' AND isnull(atom.hora_registro,'') = '' AND isnull(buzR.usuario_propietario, '') != '' THEN '0005' WHEN solf.estatus = 'EXCEPCION_GARANTIA' AND isnull(solf.descripcion, ' ') = 'APROBADA' AND isnull(aseR.persona, '') != '' AND isnull(ltrim(solf.contrato),'') = '' THEN '0005' WHEN solf.estatus = 'ENVIO_CEC' AND isnull(atom.hora_registro,'') != '' THEN '0007' WHEN solf.estatus = 'ASIGNACION_CEC' AND isnull(solf.descripcion,'') = 'SUCU_CORRE' THEN '0007' ELSE solf.estatus END AS estatusRealCod,
(SELECT COUNT(1) FROM atomos WHERE proceso = 'OBSERVACIONES_CEC' AND codigo_resolucion = 'EVCR_ERROR' AND solicitud = solf.solicitud ) AS numeroVueltas, tec.fecha_inicio_gestion AS fechaInicioGestion, CASE WHEN solf.express = 'S' THEN 'N/A' ELSE isnull(CONVERT(NVARCHAR, datediff(minute, tec.fecha_inicio_gestion, getdate())), '') END AS estatusRevision, buzR.fecha_termino AS fechaTermino,
ISNULL(teca.observaciones,'') AS motivoRechazo, CASE WHEN ISNULL(teca.autoriza_regional, ISNULL(teca.autoriza_divisional,teca.autoriza_riesgos)) IS NOT NULL THEN pers.nombre + ' ' + pers.apellido_pat + ' ' + pers.apellido_mat WHEN ISNULL(teca.autoriza_divisional, ISNULL(teca.autoriza_riesgos,teca.autoriza_regional)) IS NOT NULL THEN pers.nombre + ' ' + pers.apellido_pat + ' ' + pers.apellido_mat WHEN ISNULL (teca.autoriza_riesgos,ISNULL(teca.autoriza_regional, teca.autoriza_divisional)) IS NOT NULL THEN pers.nombre + ' ' + pers.apellido_pat + ' ' + pers.apellido_mat ELSE pers.nombre + ' ' + pers.apellido_pat + ' ' + pers.apellido_mat END AS usuarioOperacion,
CASE WHEN ISNULL(teca.fecha_regional, ISNULL(teca.fecha_divisional, teca.fecha_riesgos)) IS NOT NULL THEN ISNULL(CONVERT(VARCHAR, teca.fecha_regional, 103), '') WHEN ISNULL(teca.fecha_divisional, ISNULL(teca.fecha_riesgos ,teca.fecha_regional)) IS NOT NULL THEN ISNULL(CONVERT(VARCHAR, teca.fecha_divisional, 103), '') WHEN ISNULL(teca.fecha_riesgos, ISNULL(teca.fecha_regional, teca.fecha_divisional)) IS NOT NULL THEN ISNULL(CONVERT(VARCHAR, teca.fecha_riesgos, 103), '') ELSE ISNULL(CONVERT(VARCHAR, teca.fecha_riesgos, 103), '') END AS fechaOperacion,
CASE WHEN ISNULL(teca.fecha_regional, ISNULL(teca.fecha_divisional, teca.fecha_riesgos)) IS NOT NULL THEN ISNULL(CONVERT(VARCHAR, teca.fecha_regional, 108), '') WHEN ISNULL(teca.fecha_divisional, ISNULL(teca.fecha_riesgos ,teca.fecha_regional)) IS NOT NULL THEN ISNULL(CONVERT(VARCHAR, teca.fecha_divisional, 108), '') WHEN ISNULL(teca.fecha_riesgos , ISNULL(teca.fecha_regional, teca.fecha_divisional)) IS NOT NULL THEN ISNULL(CONVERT(VARCHAR, teca.fecha_riesgos, 108), '') ELSE ISNULL(CONVERT(VARCHAR, teca.fecha_riesgos, 108), '') END AS horaOperacion,
ISNULL(CONVERT(VARCHAR, atomc.hora_registro, 103), '') AS fechaAutorizacion, ISNULL(CONVERT(VARCHAR, atomc.hora_registro, 108), '') AS horaAutorizacion,
ISNULL(CONVERT(VARCHAR, tfe.fecha_envio_regional, 103), '') + ' ' + ISNULL(CONVERT(VARCHAR, tfe.fecha_envio_regional, 108), '') AS fechaEnvioReg, ISNULL(CONVERT(VARCHAR, tfe.fecha_respuesta_regional, 103), '') + ' ' + ISNULL(CONVERT(VARCHAR, tfe.fecha_respuesta_regional, 108), '') AS fechaRespuestaReg, ISNULL(CONVERT(VARCHAR, tfe.fecha_envio_divisional, 103), '') + ' ' + ISNULL(CONVERT(VARCHAR, tfe.fecha_envio_divisional, 108), '') AS fechaEnvioDiv, ISNULL(CONVERT(VARCHAR, tfe.fecha_respuesta_divisional, 103), '') + ' ' + ISNULL(CONVERT(VARCHAR, tfe.fecha_respuesta_divisional, 108), '') AS fechaRespuestaDiv, ISNULL(CONVERT(VARCHAR, tfe.fecha_envio_subdirector, 103), '') + ' '+ ISNULL(CONVERT(VARCHAR, tfe.fecha_envio_subdirector, 108), '') AS fechaEnvioSubComercial, ISNULL(CONVERT(VARCHAR, tfe.fecha_respuesta_subdirector, 103), '') + ' ' + ISNULL(CONVERT(VARCHAR, tfe.fecha_respuesta_subdirector, 108), '')AS fechaRespuestaSubComercial
FROM solicitudes_finsol solf INNER JOIN personas grup ON (grup.persona = solf.cliente) INNER JOIN sucursales sucu ON (sucu.sucursal = solf.oficina) INNER JOIN productos prod ON (prod.producto = solf.codigo_producto) INNER JOIN regiones zona ON (zona.region = sucu.region) INNER JOIN divisiones divi ON (divi.division = zona.division) LEFT JOIN sucursales_tipificaciones suct ON (suct.sucursal = solf.oficina AND suct.tipificacion = 'TFTS' ) LEFT JOIN codigos tipi ON (tipi.codigo = suct.valor_tipificacion AND tipi.tipo_codigo = 'TFTS') LEFT JOIN relacion_clave_finsol rcvf ON (rcvf.persona = solf.cliente) LEFT JOIN codigos camp ON (camp.codigo = solf.campana AND camp.tipo_codigo = 'CECG') LEFT JOIN contrato_clave_finsol ccvf ON (ccvf.contrato = solf.contrato) LEFT JOIN codigos excep ON (excep.tipo_codigo = 'SECG' AND excep.codigo = solf.senalamiento_excepcion) LEFT JOIN buzon_tareas buzR ON (buzR.descripcion = solf.solicitud AND buzR.tipo_tarea = 'TECR' AND buzR.perfil = 'PCEC' ) LEFT JOIN personas aseR ON (aseR.persona = buzR.usuario_propietario) LEFT JOIN atomos atomA ON (atomA.solicitud = solf.solicitud AND atomA.proceso = 'GENERA_CONTRATO') LEFT JOIN personas aseA ON (aseA.persona = atomA.usuario_genero_atomo) LEFT JOIN atomos atom ON (atom.solicitud = solf.solicitud AND atom.proceso = 'OBSERVACIONES_CEC' AND atom.codigo_resolucion = 'EVCR_ERROR' AND atom.id_atomo = ( SELECT min(a.id_atomo) FROM atomos a WHERE a.solicitud= solf.solicitud AND a.proceso = 'OBSERVACIONES_CEC' AND a.codigo_resolucion = 'EVCR_ERROR' )) LEFT JOIN tareas_evaluacion_credito tec ON tec.solicitud = solf.solicitud LEFT JOIN buzon_tarea_reasigna btr ON btr.id_tarea = buzR.id_tarea AND fecha_reasigna = (SELECT max(r.fecha_reasigna) FROM buzon_tarea_reasigna r WHERE r.id_tarea = buzR.id_tarea ) LEFT JOIN personas prea ON prea.persona = btr.usuario_propietario LEFT JOIN atomos atomu ON (atomu.solicitud = solf.solicitud AND atomu.proceso = 'OBSERVACIONES_CEC' AND atomu.codigo_resolucion = 'EVCR_ERROR' AND atomu.id_atomo = ( SELECT max(b.id_atomo) FROM atomos b WHERE b.solicitud= solf.solicitud AND b.proceso = 'OBSERVACIONES_CEC' AND b.codigo_resolucion = 'EVCR_ERROR' )) LEFT JOIN contratos contOP ON contOP.contrato = solf.contrato_ant LEFT JOIN solicitudes_finsol solfOP ON solfOP.solicitud = contOP.solicitud LEFT JOIN tareas_excepcion_campania teca ON (teca.solicitud = solf.solicitud AND teca.id_tarea = ( SELECT max(id_tarea) FROM tareas_excepcion_campania tareas WHERE tareas.solicitud = solf.solicitud AND tareas.codigo_estatus IN ('APRO', 'RECH') ))
LEFT JOIN atomos atomc ON (atomc.solicitud = solf.solicitud AND atomc.proceso = 'EXCEPCION_CUPO_MONTO' AND atomc.codigo_resolucion IN ('APROBADA','RECHAZADA') AND atomc.id_atomo = ( SELECT max(c.id_atomo) FROM atomos c WHERE c.solicitud= solf.solicitud AND c.proceso = 'EXCEPCION_CUPO_MONTO' AND c.codigo_resolucion IN ('APROBADA','RECHAZADA') )) LEFT JOIN personas pers ON pers.persona = ISNULL(teca.autoriza_regional, ISNULL(teca.autoriza_divisional, teca.autoriza_riesgos)) LEFT JOIN tareas_excepcion_campania tecampania ON tecampania.solicitud = solf.solicitud LEFT JOIN tareas_firma_electronica tfe ON tfe.solicitud = solf.solicitud WHERE
solf.fecha_captura BETWEEN #{fechaInicial} AND #{fechaFinal}
AND solf.categoria = #{renovacion}
AND buzR.usuario_propietario = #{analista}
AND divi.division = #{division}
AND zona.region = #{region}
AND solf.oficina = #{sucursal}
AND solf.codigo_producto = #{producto}
AND solf.estatus = #{estatus}
AND solf.ciclo = #{ciclo} - 1

SELECT buro.codigoProducto, buro.descripcionProducto, sum(buro.cumpleValidacion) AS cumpleValidacion, cumpleValidacionPorcentaje = CASE WHEN (sum(buro.cumpleValidacion) + sum(buro.noCumpleValidacion)) = 0 THEN 0 ELSE (sum(buro.cumpleValidacion) * 100.0) / (sum(buro.cumpleValidacion) + sum(buro.noCumpleValidacion)) end, sum(buro.noCumpleValidacion) AS noCumpleValidacion, noCumpleValidacionPorcentaje = CASE WHEN (sum(buro.cumpleValidacion) + sum(buro.noCumpleValidacion)) = 0 THEN 0 ELSE (sum(buro.noCumpleValidacion) * 100.0) / (sum(buro.cumpleValidacion) + sum(buro.noCumpleValidacion)) end, sum(buro.cumpleValidacion) + sum(buro.noCumpleValidacion) AS total FROM (SELECT prod.producto AS codigoProducto, prod.descripcion AS descripcionProducto, cumpleValidacion = isnull(CASE WHEN atom.codigo_resolucion = 'REBU_OK' THEN 1 ELSE 0 END, 0), noCumpleValidacion = isnull(CASE WHEN atom.codigo_resolucion = 'REBU_ERRCA' THEN 1 ELSE 0 END, 0 ), solf.oficina AS sucursal, atom.hora_registro AS fechaFiltro, solf.ciclo AS ciclo FROM atomos atom INNER JOIN solicitudes_finsol solf ON (atom.solicitud = solf.solicitud ) INNER JOIN productos prod ON (prod.producto = solf.codigo_producto) WHERE atom.proceso = 'BURO' ) buro INNER JOIN sucursales sucu ON (buro.sucursal = sucu.sucursal ) INNER JOIN regiones reg ON (reg.region = sucu.region ) INNER JOIN divisiones dvi ON (dvi.division = reg.division ) WHERE buro.fechaFiltro BETWEEN #{fechaInicial} AND #{fechaFinal}
AND dvi.division = #{division}
AND reg.region = #{region}
AND buro.sucursal = #{sucursal}
AND buro.codigoProducto = #{producto}
AND buro.ciclo = #{ciclo}
GROUP BY buro.codigoProducto, buro.descripcionProducto

SELECT producto = prod.descripcion, numeroAnalista = isnull(p.persona, '0'), analista = p.nombre + ' ' + p.apellido_pat + ' ' + p.apellido_mat, sucursal = s.nombre, solicitud = bt.descripcion, nombreGrupo = p1.nombre, ISNULL(sf.ciclo, 0) + 1 AS ciclo, fechaAsignacion = ISNULL(Convert(nvarchar, bt.fecha_asignacion, 103), '') + ' ' + ISNULL(Convert(nvarchar, bt.fecha_asignacion, 108), ''), fechaResolucion = ISNULL(Convert(nvarchar, bt.fecha_termino, 103), '') + ' ' + ISNULL(Convert(nvarchar, bt.fecha_termino, 108), ''), isnull(sf.estatus, '') AS estatus, ponderacion = isnull(tec.ponderacion, 0) FROM buzon_tareas bt INNER JOIN solicitudes_finsol sf ON rtrim(ltrim(sf.solicitud)) = rtrim(ltrim(bt.descripcion)) LEFT JOIN personas p ON p.persona = bt.usuario_propietario LEFT JOIN sucursales s ON s.sucursal = bt.sucursal LEFT JOIN personas p1 ON p1.persona = sf.cliente LEFT JOIN productos prod ON prod.producto = sf.codigo_producto LEFT JOIN tareas_evaluacion_credito tec ON tec.solicitud = sf.solicitud WHERE bt.tipo_tarea = 'TECR' AND bt.perfil = 'PCEC' AND bt.fecha_asignacion BETWEEN #{fechaInicial} AND #{fechaFinal}
AND bt.sucursal = #{sucursal}
AND sf.codigo_producto = #{producto}
ORDER BY bt.id_tarea

SELECT producto=prod.descripcion, numeroAnalistaAnterior = p.persona, analistaAnterior = p.nombre + ' ' + p.apellido_pat + ' ' + p.apellido_mat, numeroAnalistaNuevo = p2.persona, analistaNuevo = p2.nombre + ' ' + p2.apellido_pat + ' ' + p2.apellido_mat, sucursal= s.nombre, solicitud=bt.descripcion, nombreGrupo = p1.nombre, ISNULL(sf.ciclo, 0) + 1 AS ciclo, fechaAsignacion = ISNULL(Convert(nvarchar, bt.fecha_asignacion, 103), '') + ' ' + ISNULL(Convert(nvarchar, bt.fecha_asignacion, 108), ''), fechaResolucion = ISNULL(Convert(nvarchar, bt.fecha_termino, 103), '') + ' ' + ISNULL(Convert(nvarchar, bt.fecha_termino, 108), ''), ponderacion = isnull(tec.ponderacion, 0) FROM buzon_tareas bt INNER JOIN solicitudes_finsol sf ON rtrim(ltrim(sf.solicitud)) = rtrim(ltrim(bt.descripcion)) INNER JOIN buzon_tarea_reasigna btr ON btr.id_tarea = bt.id_tarea LEFT JOIN personas p ON p.persona = btr.usuario_propietario LEFT JOIN personas p2 ON p2.persona = btr.usuario_propietario_nuevo LEFT JOIN sucursales s ON s.sucursal = bt.sucursal LEFT JOIN personas p1 ON p1.persona = sf.cliente LEFT JOIN productos prod ON prod.producto = sf.codigo_producto LEFT JOIN tareas_evaluacion_credito tec ON tec.solicitud = sf.solicitud WHERE bt.tipo_tarea = 'TECR' AND bt.fecha_asignacion BETWEEN #{fechaInicial} AND #{fechaFinal}
AND bt.sucursal = #{sucursal}
AND sf.codigo_producto = #{producto}
ORDER BY bt.id_tarea

