SELECT rcvf.clave_cu AS customerId, pers.nombre AS firstName, '' AS otherName, pers.apellido_pat AS secondName, pers.apellido_mat AS lastName, pers.sexo AS gender, ISNULL( Convert(nvarchar,pers.fecha_nacimiento,103), '') AS birthdate, pers.rfc AS rfc, pers.persona as persona, ultimoContrato = convert(VARCHAR,max(convert(DECIMAL,ig.contrato))), ultimoLD = ccf.contrato_LD, nombreGrupo = pg.nombre, monto = ig.monto_otorgado FROM personas pers INNER JOIN relacion_clave_finsol rcvf ON pers.persona = rcvf.persona INNER JOIN persona_complemento pc ON pc.persona = pers.persona AND isnull(pc.codigo_bloqueo,'') = '' LEFT JOIN integrante_grupo ig ON ig.integrante = pers.persona LEFT JOIN contratos c ON c.contrato = ig.contrato LEFT JOIN contrato_clave_finsol ccf ON ccf.contrato = c.contrato LEFT JOIN personas pg ON pg.persona = c.cliente AND rcvf.clave_cu IS NOT null WHERE 1=1 AND pers.personalidad = 'F'
AND pers.nombre LIKE #{pNombre}||'%'
AND pers.apellido_pat = #{pAPaterno}
AND pers.apellido_mat = #{pAMaterno}
AND pers.rfc LIKE #{rfc}||'%'
AND pers.fecha_nacimiento = #{fechaNacimiento}
AND pers.persona = #{numeroPersona}
AND ccf.contrato IN (SELECT convert(VARCHAR,max(convert(DECIMAL,ia.contrato))) FROM contratos ca, integrante_grupo ia WHERE ca.contrato = ia.contrato AND ca.producto <> 'GOS1' AND ia.integrante = pers.persona) GROUP BY rcvf.clave_cu, pers.nombre, pers.apellido_pat, pers.apellido_mat, pers.sexo, pers.fecha_nacimiento, pers.rfc, pers.persona, pg.nombre, ccf.contrato_LD, ig.monto_otorgado

