SELECT bt.id_tarea AS idTarea, solf.contrato AS contrato, prod.descripcion AS producto, solf.efectivo_solicitado AS montoSolicitado, bt.descripcion AS solicitud, pers.nombre AS nombreGrupo, solf.cliente AS cliente, suc.sucursal AS numeroSucursal, suc.nombre AS sucursal, div.division AS numeroDivision, div.nombre AS division, solf.ciclo AS ciclo, zona.nombre AS region, zona.region AS numeroRegion, cod.descripcion AS campana, bt.tipo_tarea AS tipoTarea, tex.justificacion AS justificacion, CONVERT(varchar(10), tex.fecha_autorizacion, 103) AS fechaAutorizacion, CONVERT(varchar(10), solf.fecha_captura, 103) AS fechaCaptura, CONVERT(varchar(10), bt.fecha_alta, 103) AS fechaIngreso, datediff(mi, bt.fecha_alta , getdate()) AS espera, tex.observaciones AS observaciones, isnull(solf.pct_garantia,0) AS pctGarantia, isnull(c.monto,0) AS montoCreditoAnterior, isnull(cc.valor,'0') AS garantiaCreditoAnterior, isnull(solf.express,'N') AS express
FROM buzon_tareas bt
LEFT JOIN tareas_excepcion_garantia tex ON tex.id_tarea = bt.id_tarea LEFT JOIN solicitudes_finsol solf ON bt.descripcion = solf.solicitud LEFT JOIN codigos cod ON solf.campana = cod.codigo AND tipo_codigo = 'CECG' LEFT JOIN productos prod ON solf.codigo_producto = prod.producto LEFT JOIN personas pers ON pers.persona = solf.cliente LEFT JOIN sucursales suc ON (suc.sucursal = bt.sucursal) LEFT JOIN regiones zona ON (zona.region = suc.region) LEFT JOIN divisiones div ON (div.division = zona.division) LEFT JOIN contratos c ON c.contrato = solf.contrato_ant LEFT JOIN condiciones_contrato cc ON cc.contrato = c.contrato AND cc.clave_condicion = 'PCT_DEP_GARANTIA'
WHERE div.division = #{division}
AND zona.region = #{region}
AND bt.perfil = 'PCOM' AND bt.tipo_tarea = 'TOEG' AND bt.status = 'P' ORDER BY espera desc

